onfig->vk_comments_photo) 		$attach[] = "photo";		if($config->vk_comments_video) 		$attach[] = "video";		if($config->vk_comments_audio) 		$attach[] = "audio";		if($config->vk_comments_link) 		$attach[] = "link";				if(count($attach) == 5) {			$config->vk_comments_attached = "*";		}		else {			$config->vk_comments_attached = "";			$c = count($attach)-1;			for($a=0;$a<=$c;$a++) {				$config->vk_comments_attached .= $attach[$a];				if($a != $c) $config->vk_comments_attached .= ",";			}		}	}	// Load template	$module['vk_comments'] = $tpl->load_template("module_vk_comments", true);}?>