<?phpclass tpl_items_user_avatar {//#########################################################//#		Основной шаблон//#########################################################function tpl_page() {$HTML = <<<HTML	<h1>Аватары</h1>	<div id="content">	Страница: {html:pages}	<br /><font class="rem">Наведите мышку на аватар что бы разглядеть его как следует или кликните на него</font>	<ul class="hoverbox">	{html:avatars}	</ul>	</div>HTML;return $HTML;}//*****************************************************// CSSfunction tpl_css() {$CSS = <<<CSS#avatar {	border: 1px solid #cccccc;	padding: 5px; margin: 2px;	background-image: url(img/av_bg.png);	display: inline-block; position: relative;}.hoverbox { cursor: default; list-style: none; }.hoverbox .preview { border: 11px solid #A3B0BC; height: 100px; width: 100px; }.hoverbox a { cursor: default; }.hoverbox a .preview { display: none; }.hoverbox a:hover .preview { display: block; left: -25px; position: absolute; top: -25px; z-index: 100; }.hoverbox img { background: #fff; border-color: #aaa #ccc #ddd #bbb; border-style: solid; border-width: 1px; color: inherit; height: 60px; padding: 2px; vertical-align: top; width: 60px; }.hoverbox li { background: #eee; border-color: #ddd #bbb #aaa #ccc; border-style: solid; border-width: 1px; color: inherit; display: inline; float: left; margin: 3px; padding: 5px; position: relative; }CSS;return $CSS;}//*****************************************************// JSfunction tpl_js() {$JS = <<<JS<script type="text/javascript">	$(document).ready(function(){		$('.preview').corner("round 2px");		$("a[rel='av']").colorbox({width:"300px", height:"300px"});	});</script>JS;return $JS;}//#########################################################//#		Элементы шаблона//#########################################################//***************************//	Отображение аватара//	{html:avatars}function avatars($src, $width=100, $height=100) {$HTML = <<<HTML	<li><a rel="av" href="{$src}"><img src="{$src}" border="0"><img src="{$src}" border="0" class="preview" ></a></li>HTML;return $HTML;}//***************************//	Отрисовка отображения страниц //	{html:pages}function pages($src, $pages) {$HTML = <<<HTML		<a href="{$src}" class="linkb">{$pages}</a>HTML;return $HTML;}//***************************//	Разделитель рядов аватаров //	используется динамически.function avatars_line() {$HTML = <<<HTML	<br />HTML;return $HTML;}}?>