$(document).ready(function(){$("a[rel='img']").colorbox({maxWidth:"98%",maxHeight:"98%",retinaImage:true,retinaUrl:true,onComplete:function(){$("#cboxLoadedContent").zoom({on:"click"})}});$("a[rel='html']").colorbox({maxWidth:"75%",maxHeight:"90%"});$("a[rel='iframe']").colorbox({width:"75%",height:"75%",iframe:true});$("[rel='tooltip']").tooltip();$("[rel='popover']").popover();$(".alert").alert();$(".affix").affix();if(/Android|webOS|iPhone|iPad|iPod|BlackBerry/i.test(navigator.userAgent)){$(".selectpicker").selectpicker("mobile")}else{$(".selectpicker").selectpicker()}$(".datepicker").datepicker({format:"dd.mm.yyyy",language:"ru",todayHighlight:true});$(document).bind("cbox_open",function(){$("#colorbox").swipe({swipeLeft:function(b,d,e,c,a){jQuery.colorbox.next()},swipeRight:function(b,d,e,c,a){jQuery.colorbox.prev()},threshold:0})});$(".carousel").swipe({swipe:function(c,e,f,d,a,b){if(e==="left"){$(this).carousel("next")}if(e==="right"){$(this).carousel("prev")}},allowPageScroll:"vertical"});$(window).scroll(function(){if($(this).scrollTop()>100){$("a#move_top").fadeIn()}else{$("a#move_top").fadeOut(400)}});$("a#move_top").click(function(){$("html, body").animate({scrollTop:0},"500","swing");return false})});